import React from "react";

class ReadBalance extends React.Component {
  state = { dataKey: null };


  componentDidMount() {
    const { drizzle, drizzleState } = this.props;
    const contract = drizzle.contracts.TestMinter;
    const dataKey = contract.methods["decimals"].cacheCall();
    this.setState({ dataKey });
  }

  render() {
    // get the contract state from drizzleState
    const { TestMinter } = this.props.drizzleState.contracts;

    // using the saved `dataKey`, get the variable we're interested in
    const decimals = TestMinter.decimals[this.state.dataKey];

    // if it exists, then we display its value
    return <p>Number of decimals: {decimals && decimals.value}</p>;
  }
}

export default ReadBalance;
